// Helper function for rating stars
function get_rating_stars($rating, $max = 5, $size = 16) {
    $output = '<span class="rating-stars">';
    $full = floor($rating);
    $half = ($rating - $full >= 0.5) ? 1 : 0;
    $empty = $max - $full - $half;

    for ($i = 0; $i < $full; $i++) {
        $output .= get_icon('star', $size, 'star-filled');
    }

    if ($half) {
        $output .= get_icon('star', $size, 'star-half');
    }

    for ($i = 0; $i < $empty; $i++) {
        $output .= get_icon('star-empty', $size, 'star-empty');
    }

    $output .= '</span>';
    return $output;
}
